<template>
	<div>
		
		<h1 class="title">
			<a href="/" class="active">少数派</a>
		</h1>
		<section>	
			<div class="login-form">
					<p class="inputcontainer">
						<input type="tel" name="phone" placeholder="手机号码" autocomplete="off" class="el-input__inner" v-model="phone">
					</p>
					<p class="inputcontainer">
						<input type="text" name="name" placeholder="昵称" class="text" v-model="name">
					</p>
					<p class="inputcontainer">
						<input type="password" name="password" placeholder="密码" class="text" v-model="password">
					</p>
					<p class="inputcontainer">
						<input type="password" name="passwordAgain" placeholder="确认密码" class="text" v-model="passwordAgain">
					</p>
					<label class="checkbox">
						<input type="checkbox" class="checkbox" v-model="isSlected" @click="check()"> 
						我已经阅读并同意......
						<span class="red" v-show="notSlected">请阅读并同意相关协议</span>
					</label>
					<a  class="btn form" @click="postUserinfo()">注册</a>
					<div class="warn" v-show="isShow">
						<span  @click="isShow=!isShow">×</span>	
						<p>
							请确保您的手机号码或者昵称不能为空,或者确保密码与确认密码相同
						</p>
					</div>
					<div class="warn userhaved" v-if="isJoin">
						<span  @click="isJoin=!isJoin">×</span>	
						<p>
							请确保您的手机号码未被注册
						</p>
					</div>
			</div>
		</section>
	</div>
</template>

<script type="text/javascript">
	import axios from 'axios';

	export default{

		data(){

			return {
					phone:'',
					name:'',
					password:'',
					passwordAgain:'',
					isShow:false,
					isJoin:false,
					isSlected:false,
					notSlected:false


			}

		},
		methods:{

			postUserinfo(){

				// console.log(this.phone.length);
				if (this.isSlected == false) {

					this.notSlected=true;

				}

				if (this.phone.length == 11 && this.name !== '' && this.password !== '' && this.password == this.passwordAgain) {

					if (this.isSlected == false) {

						this.notSlected = true;

					}else{

						axios.post('/userinfo/join',
							'phone=' + this.phone +'&name=' + this.name + '&password=' + this.password).then(res=>{

							// console.log(res.data)
							if (res.data.result == 0) {

								this.$router.push({path:'/login'});

							}else{

								this.$router.push({path:'/join'});
								this.isJoin = true;

							}

						})
						
					}

					

				}else{

					this.isShow = true;

				}


				if ( this.password !== this.passwordAgain) {

					this.isShow = true;
					this.password = '';
					this.passwordAgain = '';
				}



			},
			check(){

				this.notSlected=false;

			}

		}


	}


</script>

